const smoothScroll=document.querySelectorAll(".nav__link"),burger=document.querySelector(".burger"),nav=document.querySelector(".nav"),navItem=document.querySelectorAll(".nav__link"),body=document.body;smoothScroll.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault();e=e.target.getAttribute("href").substr(1);document.getElementById(e).scrollIntoView({behavior:"smooth",block:"start"})})}),burger.addEventListener("click",()=>{body.classList.toggle("none-scroll"),burger.classList.toggle("burger_active"),nav.classList.toggle("nav_visible")}),navItem.forEach(e=>{e.addEventListener("click",()=>{body.classList.remove("none-scroll"),burger.classList.remove("burger_active"),nav.classList.remove("nav_visible")})});