const smoothScroll=document.querySelectorAll(".nav__link"),burger=document.querySelector(".burger"),navMenu=document.querySelector(".nav"),toggleMenu=(smoothScroll.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault();e=e.target.getAttribute("href").substr(1);document.getElementById(e).scrollIntoView({behavior:"smooth",block:"start"})})}),()=>{navMenu.classList.toggle("nav_active"),burger.classList.toggle("burger_active")});burger.addEventListener("click",()=>{toggleMenu()});